const form = document.querySelector("form"),
    emailInput = document.querySelector('[name="email"]'),
    emailLabel = document.getElementById("emailLabel"),
    divFormulaire = document.getElementById("formulaire"),
    allConfirmation = document.querySelectorAll(".confirmation"),
    listeAdresse = document.getElementById("listeAdresse"),
    adresse = document.querySelector('[name="adresse"]'),
    ville = document.querySelector('[name="ville"]'),
    code_postal = document.querySelector('[name="code_postal"]'),
    valider = document.getElementById("valider"),
    modifier = document.getElementById("modifier");
var confirmation = !1,
    timeout = null;

function check() {
    form.reportValidity(), emailInput.setCustomValidity(""), form.checkValidity() && request(`${href}/request/checkmail?q=${emailInput.value}`).then(response => {
        (response = JSON.parse(response)) ? (emailInput.setCustomValidity("cette email est déja enregistré. Veuillez en saisir une nouvelle"), emailLabel.removeAttribute("hidden")) : emailLabel.setAttribute("hidden", ""), form.reportValidity(), form.checkValidity() && show()
    })
}

function show() {
    if (!confirmation) { let formData = Object.fromEntries(new FormData(form)); for (const [key, value] of Object.entries(formData)) value && (document.getElementById(key).innerHTML = value) }
    allConfirmation.forEach(element => { element.style.display = confirmation ? "none" : "block" }), divFormulaire.style.display = confirmation ? "block" : "none", confirmation = !confirmation
}

function searchAdresse() {
    clearTimeout(timeout), timeout = setTimeout(() => {
        const url = `https://api-adresse.data.gouv.fr/search/?q=${adresse.value}&limit=5&autocomplete=1`;
        request(url).then(response => { listeAdresse.innerHTML = "", JSON.parse(response).features.forEach(element => { element = element.properties, listeAdresse.innerHTML += `<p onclick="complete(this)" adresse="${element.name}" ville="${element.city}" code="${element.postcode}">${element.label}</p>` }) })
    }, 500)
}

function complete(element) { adresse.value = element.getAttribute("adresse"), ville.value = element.getAttribute("ville"), code_postal.value = element.getAttribute("code"), listeAdresse.style.display = "none" }

function hiddenListe() { setTimeout(() => { listeAdresse.style.display = "none" }, 100) }

function showListe() { listeAdresse.style.display = "block" }
valider.addEventListener("click", check), modifier.addEventListener("click", show), adresse.addEventListener("input", searchAdresse), adresse.addEventListener("blur", hiddenListe), adresse.addEventListener("focus", showListe);